import React, { useMemo, useState } from 'react';
import { MedicalQualityFinding, KnowledgeFlagSeverity } from '../types';

interface Props {
  score?: number;
  findings?: MedicalQualityFinding[];
  reasoningFindings?: MedicalQualityFinding[];
}

const getStatus = (value?: number) => {
  if (value === undefined || value === null) {
    return { label: 'לא זמין', color: 'text-slate-500' };
  }
  if (value >= 75) return { label: 'גבוהה', color: 'text-emerald-600' };
  if (value >= 55) return { label: 'בינונית', color: 'text-amber-600' };
  return { label: 'נמוכה', color: 'text-rose-600' };
};

const severityLabel: Record<KnowledgeFlagSeverity, string> = {
  critical: 'קריטי',
  warning: 'אזהרה',
  info: 'מידע',
};

const severityStyle: Record<KnowledgeFlagSeverity, string> = {
  critical: 'text-rose-700',
  warning: 'text-amber-700',
  info: 'text-slate-600',
};

const assertionBadge: Record<string, { label: string; className: string }> = {
  FACT: { label: 'עובדה', className: 'bg-emerald-50 text-emerald-700' },
  INTERPRETATION: { label: 'פרשנות מקצועית', className: 'bg-amber-50 text-amber-700' },
  POSSIBILITY: { label: 'אפשרות / השערה', className: 'bg-slate-100 text-slate-600' },
};

const LEGAL_DISCLAIMER_TEXT = 'המערכת אינה מחליפה מומחה רפואי. זהו כלי תומך החלטה משפטית בלבד.';

const domainLabel: Record<string, string> = {
  GENERAL: 'כללי',
  ORTHO: 'אורתופדיה',
  NEURO: 'נוירולוגיה',
  CARDIO: 'קרדיולוגיה',
  PSYCH: 'פסיכיאטריה',
  REHAB: 'שיקום',
  DENTAL: 'רפואת שיניים',
  ENT: 'אף־אוזן־גרון',
  GASTRO: 'גסטרואנטרולוגיה',
  OBGYN: 'מיילדות וגינקולוגיה',
  EMERGENCY: 'רפואה דחופה',
  ICU: 'טיפול נמרץ',
  GENERAL_SURGERY: 'כירורגיה כללית',
  PLASTIC_SURGERY: 'כירורגיה פלסטית',
  COSMETIC_INJECTABLES: 'אסתטיקה/הזרקות',
};

const MedicalQualityBox: React.FC<Props> = ({ score, findings = [], reasoningFindings = [] }) => {
  const status = getStatus(score);
  const [reasoningFilter, setReasoningFilter] = useState<'all' | 'critical'>('all');
  const filteredReasoning = useMemo(
    () =>
      reasoningFilter === 'critical'
        ? reasoningFindings.filter((finding) => finding.severity === 'critical')
        : reasoningFindings,
    [reasoningFilter, reasoningFindings],
  );

  const renderAssertionBadge = (type?: string, rationale?: string) => {
    if (!type) return null;
    const config = assertionBadge[type] ?? { label: type, className: 'bg-slate-100 text-slate-600' };
    return (
      <span className={`inline-flex items-center rounded px-2 py-0.5 text-[11px] font-semibold ${config.className}`} title={rationale}>
        {config.label}
      </span>
    );
  };

  return (
    <div className="rounded border border-slate-200 bg-white">
      <div className="flex items-center justify-between px-4 py-3 border-b border-slate-100">
        <div>
          <div className="text-sm font-bold text-slate-700">איכות חוות הדעת הרפואית</div>
          <div className="text-xs text-slate-500">מדד אינדיקטיבי המבוסס על טענות קיימות בלבד</div>
        </div>
        <div className="text-right">
          <div className={`text-sm font-semibold ${status.color}`}>{status.label}</div>
          <div className="text-2xl font-bold text-slate-800">{score !== undefined ? score : '—'}</div>
          <div className="text-[10px] uppercase text-slate-400 tracking-wide">/100</div>
        </div>
      </div>
      <div className="p-4 space-y-3 text-sm text-slate-700">
        {findings.length === 0 ? (
          <div className="rounded border border-slate-100 bg-slate-50 px-3 py-2 text-slate-500">
            לא אותרו ליקויים משמעותיים בחוות הדעת.
          </div>
        ) : (
          <ul className="space-y-2">
            {findings.map((finding) => (
              <li key={`${finding.code}-${finding.message}`} className="rounded border border-slate-100 bg-slate-50 px-3 py-2">
                <div className="flex items-center justify-between">
                  <span className={`text-xs font-semibold ${severityStyle[finding.severity]}`}>
                    {severityLabel[finding.severity] ?? finding.severity}
                  </span>
                  <span className="text-[11px] text-slate-400">{finding.code}</span>
                </div>
                <div className="mt-1 flex flex-wrap items-center gap-2 text-[11px] text-slate-500">
                  {finding.domain && (
                    <span className="font-semibold uppercase tracking-wide">
                      {domainLabel[finding.domain] ?? finding.domain}
                    </span>
                  )}
                  {renderAssertionBadge(finding.assertionType, finding.reliability?.rationale)}
                </div>
                <p className="mt-1 text-slate-700">{finding.message}</p>
                {finding.basis && finding.basis.length > 0 && (
                  <div className="mt-1 text-[11px] text-slate-500">מבוסס על: {finding.basis[0]}</div>
                )}
                {finding.missingEvidence && finding.missingEvidence.length > 0 && (
                  <div className="mt-1 text-[11px] text-slate-600">מה חסר: {finding.missingEvidence[0]}</div>
                )}
              </li>
            ))}
          </ul>
        )}

        {reasoningFindings.length > 0 && (
          <div>
            <div className="flex items-center justify-between text-xs font-bold text-slate-600 mb-2">
              <span>בדיקות עקביות רפואית</span>
              <div className="flex items-center gap-2 text-[11px] font-normal">
                <button
                  type="button"
                  className={`rounded px-2 py-0.5 ${
                    reasoningFilter === 'all' ? 'bg-slate-200 text-slate-700' : 'text-slate-500 hover:text-slate-800'
                  }`}
                  onClick={() => setReasoningFilter('all')}
                >
                  הכל
                </button>
                <button
                  type="button"
                  className={`rounded px-2 py-0.5 ${
                    reasoningFilter === 'critical' ? 'bg-slate-200 text-slate-700' : 'text-slate-500 hover:text-slate-800'
                  }`}
                  onClick={() => setReasoningFilter('critical')}
                >
                  קריטיים בלבד
                </button>
              </div>
            </div>
            <ul className="space-y-2">
              {filteredReasoning.length === 0 && (
                <li className="rounded border border-slate-100 bg-slate-50 px-3 py-2 text-slate-500 text-xs">לא נמצאו ממצאים</li>
              )}
              {filteredReasoning.map((finding) => (
                <li key={`reason-${finding.code}-${finding.message}`} className="rounded border border-slate-100 bg-slate-50 px-3 py-2">
                  <div className="flex items-center justify-between">
                    <span className={`text-xs font-semibold ${severityStyle[finding.severity]}`}>
                      {severityLabel[finding.severity] ?? finding.severity}
                    </span>
                    <span className="text-[11px] text-slate-400">{finding.code}</span>
                  </div>
                  <div className="mt-1 flex flex-wrap items-center gap-2 text-[11px] text-slate-500">
                    {finding.domain && (
                      <span className="font-semibold uppercase tracking-wide">
                        {domainLabel[finding.domain] ?? finding.domain}
                      </span>
                    )}
                    {renderAssertionBadge(finding.assertionType, finding.reliability?.rationale)}
                  </div>
                  <p className="mt-1 text-slate-700">{finding.message}</p>
                  {finding.basis && finding.basis.length > 0 && (
                    <div className="mt-1 text-[11px] text-slate-500">מבוסס על: {finding.basis[0]}</div>
                  )}
                  {finding.missingEvidence && finding.missingEvidence.length > 0 && (
                    <div className="mt-1 text-[11px] text-slate-600">מה חסר: {finding.missingEvidence[0]}</div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
      <div className="border-t border-slate-100 px-4 py-2 text-[11px] text-slate-500">{LEGAL_DISCLAIMER_TEXT}</div>
    </div>
  );
};

export default MedicalQualityBox;
