import React from 'react';
import { KnowledgeScore } from '../types';

interface Props {
  score?: KnowledgeScore;
  label?: string;
}

const getStatus = (value: number) => {
  if (value >= 0.75) return { text: 'גבוה', classes: 'bg-emerald-100 text-emerald-700 border-emerald-300' };
  if (value >= 0.45) return { text: 'בינוני', classes: 'bg-amber-100 text-amber-700 border-amber-300' };
  return { text: 'נמוך', classes: 'bg-rose-100 text-rose-700 border-rose-300' };
};

const buildTooltip = (score?: KnowledgeScore): string => {
  if (!score) return 'אין מדד איכות';
  const rows: string[] = [`ציון כולל: ${(score.value * 100).toFixed(0)}%`];
  Object.entries(score.breakdown ?? {}).forEach(([key, value]) => rows.push(`${key}: ${(value * 100).toFixed(0)}%`));
  return rows.join(' | ');
};

const QualityBadge: React.FC<Props> = ({ score, label }) => {
  if (!score) {
    return (
      <div className="rounded border border-slate-200 bg-white p-4 text-sm text-slate-500">
        אין נתוני איכות רפואית זמינים
      </div>
    );
  }
  const status = getStatus(score.value ?? 0);
  return (
    <div
      className={`rounded border px-4 py-3 text-sm font-semibold flex items-center justify-between ${status.classes}`}
      title={buildTooltip(score)}
    >
      <span>{label ?? 'מדד איכות רפואית'}</span>
      <div className="text-lg">{(score.value * 100).toFixed(0)}%</div>
    </div>
  );
};

export default QualityBadge;
