import React, { useMemo, useState } from 'react';
import { KnowledgeClaim, KnowledgeClaimSource } from '../types';

interface Props {
  claims: KnowledgeClaim[];
  limit?: number;
}

interface ModalState {
  open: boolean;
  loading: boolean;
  error: string | null;
  source: KnowledgeClaimSource | null;
  claimTitle: string;
}

const initialModalState: ModalState = {
  open: false,
  loading: false,
  error: null,
  source: null,
  claimTitle: '',
};

const assertionLabel: Record<string, { label: string; className: string }> = {
  FACT: { label: 'עובדה', className: 'bg-emerald-50 text-emerald-700' },
  INTERPRETATION: { label: 'פרשנות מקצועית', className: 'bg-amber-50 text-amber-700' },
  POSSIBILITY: { label: 'אפשרות / השערה', className: 'bg-slate-100 text-slate-600' },
};

const LEGAL_DISCLAIMER_TEXT = 'המערכת אינה מחליפה מומחה רפואי. זהו כלי תומך החלטה משפטית בלבד.';

const formatDate = (value?: string) => {
  if (!value) return '-';
  const parsed = new Date(value);
  if (Number.isNaN(parsed.getTime())) return value;
  return parsed.toLocaleDateString('he-IL');
};

const KnowledgeClaimsPanel: React.FC<Props> = ({ claims, limit = 5 }) => {
  const [showAll, setShowAll] = useState(false);
  const [modal, setModal] = useState<ModalState>(initialModalState);

  const visibleClaims = useMemo(() => (showAll ? claims : claims.slice(0, limit)), [claims, showAll, limit]);

  const handleOpenSource = (claim: KnowledgeClaim) => {
    if (!claim.source?.snippet) {
      setModal({
        open: true,
        loading: false,
        error: 'מקור לא זמין',
        source: null,
        claimTitle: claim.type,
      });
      return;
    }
    setModal({
      open: true,
      loading: false,
      error: null,
      source: claim.source,
      claimTitle: claim.type,
    });
  };

  const closeModal = () => setModal(initialModalState);

  if (!claims.length) {
    return (
      <div className="rounded border border-slate-200 bg-white p-4 text-sm text-slate-500">
        לא אותרו טענות רפואיות במסמך זה.
      </div>
    );
  }

  return (
    <div className="rounded border border-slate-200 bg-white">
      <div className="flex items-center justify-between border-b border-slate-200 px-4 py-3">
        <h3 className="text-sm font-bold text-slate-700">טענות רפואיות</h3>
        {claims.length > limit && (
          <button className="text-xs text-blue-600 hover:text-blue-800" onClick={() => setShowAll((prev) => !prev)}>
            {showAll ? 'הצג פחות' : 'צפה בכול'}
          </button>
        )}
      </div>
      <div className="overflow-x-auto">
        <table className="min-w-full text-sm">
          <thead className="bg-slate-50 text-slate-500">
            <tr>
              <th className="px-4 py-2 text-right font-semibold">סוג</th>
              <th className="px-4 py-2 text-right font-semibold">ערך</th>
              <th className="px-4 py-2 text-right font-semibold">תאריך</th>
              <th className="px-4 py-2 text-right font-semibold">סיווג</th>
              <th className="px-4 py-2 text-right font-semibold">אמון מספרי</th>
              <th className="px-4 py-2 text-right font-semibold">איכות מקור</th>
              <th className="px-4 py-2 text-right font-semibold">מה חסר לחיזוק</th>
              <th className="px-4 py-2 text-right font-semibold">מקור</th>
            </tr>
          </thead>
          <tbody>
            {visibleClaims.map((claim) => (
              <tr key={claim.id} className="border-t border-slate-100">
                <td className="px-4 py-2 whitespace-nowrap">{claim.type}</td>
                <td className="px-4 py-2">
                  <div>{claim.value}</div>
                  {claim.basis && claim.basis.length > 0 && (
                    <div className="mt-1 text-[11px] text-slate-500">{claim.basis[0]}</div>
                  )}
                </td>
                <td className="px-4 py-2 whitespace-nowrap">{formatDate(claim.date)}</td>
                <td className="px-4 py-2 whitespace-nowrap">
                  {claim.assertionType ? (
                    <span
                      className={`inline-flex items-center rounded px-2 py-0.5 text-xs font-semibold ${
                        assertionLabel[claim.assertionType]?.className ?? 'bg-slate-100 text-slate-600'
                      }`}
                      title={claim.reliability?.rationale}
                    >
                      {assertionLabel[claim.assertionType]?.label ?? claim.assertionType}
                    </span>
                  ) : (
                    <span className="text-xs text-slate-500">—</span>
                  )}
                </td>
                <td className="px-4 py-2 whitespace-nowrap">{claim.confidence ? `${Math.round(claim.confidence * 100)}%` : '—'}</td>
                <td className="px-4 py-2 whitespace-nowrap">
                  {claim.evidenceQuality ? (
                    <span
                      className={`inline-flex items-center rounded px-2 py-0.5 text-xs font-semibold ${
                        claim.evidenceQuality === 'high'
                          ? 'bg-emerald-50 text-emerald-700'
                          : claim.evidenceQuality === 'medium'
                          ? 'bg-amber-50 text-amber-700'
                          : 'bg-rose-50 text-rose-700'
                      }`}
                      title={claim.evidenceNotes || 'הערכת איכות מקור'}
                    >
                      {claim.evidenceQuality === 'high'
                        ? 'גבוהה'
                        : claim.evidenceQuality === 'medium'
                        ? 'בינונית'
                        : 'נמוכה'}
                    </span>
                  ) : (
                    <span className="text-xs text-slate-500">—</span>
                  )}
                </td>
                <td className="px-4 py-2">
                  {claim.missingEvidence && claim.missingEvidence.length > 0 ? (
                    <span className="text-xs text-slate-600">{claim.missingEvidence[0]}</span>
                  ) : (
                    <span className="text-xs text-slate-400">—</span>
                  )}
                </td>
                <td className="px-4 py-2 text-left">
                  <button
                    className="text-xs text-blue-600 hover:text-blue-800"
                    onClick={() => handleOpenSource(claim)}
                  >
                    מקור
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="border-t border-slate-200 px-4 py-3 text-xs text-slate-500">
        הממצאים מסומנים לשימוש משפטי זהיר בלבד. {LEGAL_DISCLAIMER_TEXT}
      </div>
      {modal.open && (
        <div className="fixed inset-0 z-40 flex items-center justify-center bg-black/40 p-4">
          <div className="w-full max-w-lg rounded bg-white p-6 shadow-xl">
            <div className="mb-3 flex items-center justify-between">
              <h4 className="font-bold text-slate-700">{modal.claimTitle || 'מקור'}</h4>
              <button onClick={closeModal} className="text-sm text-slate-500 hover:text-slate-900">
                סגור
              </button>
            </div>
            {modal.loading && <div className="text-sm text-slate-500">טוען מקור...</div>}
            {!modal.loading && modal.error && <div className="text-sm text-rose-600">{modal.error}</div>}
            {!modal.loading && !modal.error && modal.source && (
              <div className="rounded border border-slate-200 bg-slate-50 p-4 text-sm text-slate-700">
                <p className="mb-2 whitespace-pre-line">{modal.source.snippet}</p>
                <div className="text-xs text-slate-500">
                  {modal.source.page ? `עמוד ${modal.source.page}` : ''}
                  {modal.source.lineRange?.length ? ` · שורות ${modal.source.lineRange.join('–')}` : ''}
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default KnowledgeClaimsPanel;
