import React from 'react';
import { CaseData } from '../types';
import { Shield, Activity } from 'lucide-react';

interface Props { caseData: CaseData; navigateToStep: (step: number) => void; }
const CaseDashboard: React.FC<Props> = ({ caseData, navigateToStep }) => {
  const activeHeads = caseData.damages.heads.filter((head) => head.isActive).length;

  return (
    <div className="space-y-8">
      <h1 className="text-3xl font-bold text-slate-800">לוח בקרה: {caseData.name}</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div
          className="bg-white p-6 rounded shadow cursor-pointer border border-slate-100 hover:border-blue-200 transition"
          onClick={() => navigateToStep(1)}
        >
          <div className="flex items-center gap-2 text-slate-600 text-sm">
            <Shield className="w-5 h-5 text-blue-600" />
            <span>ניתוח חבות</span>
          </div>
          <div className="text-3xl font-bold text-slate-900 mt-2">{caseData.liability.probability}%</div>
          <div className="flex items-center gap-2 text-xs text-slate-500 mt-3">
            <Activity className="w-4 h-4" />
            {activeHeads} ראשי נזק פעילים
          </div>
        </div>
      </div>
    </div>
  );
};
export default CaseDashboard;
