import React from 'react';
import { KnowledgeTimelineEvent } from '../types';

interface Props {
  events: KnowledgeTimelineEvent[];
}

const formatDate = (value?: string): string => {
  if (!value) return 'ללא תאריך';
  const parsed = new Date(value);
  if (Number.isNaN(parsed.getTime())) return value;
  return parsed.toLocaleDateString('he-IL');
};

const MedicalTimeline: React.FC<Props> = ({ events }) => {
  if (!events.length) {
    return (
      <div className="rounded border border-slate-200 bg-white p-4 text-sm text-slate-500">
        לא נמצאו אירועים רפואיים לציר זמן.
      </div>
    );
  }

  return (
    <div className="rounded border border-slate-200 bg-white p-4 space-y-3">
      <h3 className="text-sm font-bold text-slate-700">ציר זמן רפואי</h3>
      <ol className="space-y-3">
        {events.map((event) => (
          <li key={event.id} className="border border-slate-100 rounded p-3">
            <div className="flex items-center justify-between text-xs text-slate-500">
              <span>{formatDate(event.date)}</span>
              <span className="font-semibold text-slate-700">{event.type}</span>
            </div>
            <p className="mt-1 text-sm text-slate-800">{event.description}</p>
            {event.source?.snippet && (
              <p className="mt-2 rounded bg-slate-50 p-2 text-xs text-slate-600">{event.source.snippet}</p>
            )}
            {(event.source?.page || event.source?.lineRange) && (
              <div className="mt-1 text-xs text-slate-500">
                {event.source.page ? `עמוד ${event.source.page}` : ''}
                {event.source.lineRange ? ` · שורות ${event.source.lineRange[0]}-${event.source.lineRange[1]}` : ''}
              </div>
            )}
          </li>
        ))}
      </ol>
    </div>
  );
};

export default MedicalTimeline;
