import React from 'react';
import { KnowledgeFlag, KnowledgeFlagSeverity } from '../types';

interface Props {
  flags: KnowledgeFlag[];
}

const styles: Record<KnowledgeFlagSeverity, string> = {
  critical: 'border-rose-300 bg-rose-50 text-rose-800',
  warning: 'border-amber-300 bg-amber-50 text-amber-800',
  info: 'border-slate-200 bg-slate-50 text-slate-700',
};

const KnowledgeFlags: React.FC<Props> = ({ flags }) => {
  if (!flags.length) {
    return (
      <div className="rounded border border-slate-200 bg-white p-4 text-sm text-slate-500">
        לא אותרו דגלים במסמך זה.
      </div>
    );
  }

  return (
    <div className="rounded border border-slate-200 bg-white p-4 space-y-3">
      <h3 className="text-sm font-bold text-slate-700">דגלים רפואיים</h3>
      <ul className="space-y-2">
        {flags.map((flag, index) => (
          <li
            key={`${flag.code}-${index}`}
            className={`rounded border px-3 py-2 text-sm ${styles[flag.severity] ?? styles.info}`}
          >
            <div className="font-semibold">
              {flag.code ? `${flag.code} · ` : ''}
              {flag.message}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default KnowledgeFlags;
