import React, { useState } from 'react';
import { Shield, Lock, User } from 'lucide-react';
import { useAuth } from '../context/AuthContext';

const LoginScreen: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login, loading } = useAuth();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    try {
      await login(username, password);
    } catch (err) {
      console.error(err);
      setError('שם משתמש או סיסמה שגויים');
    }
  };

  return (
    <div className="min-h-screen bg-slate-100 flex flex-col items-center justify-center p-4 font-sans" dir="rtl">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md border border-slate-200">
        <div className="text-center mb-8">
          <div className="bg-slate-900 w-16 h-16 rounded-lg mx-auto flex items-center justify-center mb-4 text-white shadow-xl"><Shield className="w-8 h-8" /></div>
          <h1 className="text-3xl font-bold text-slate-800 serif">LexMedical</h1>
          <p className="text-slate-500 mt-2">מערכת הגנה וניהול סיכונים רפואיים</p>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
             <label className="block text-sm font-bold text-slate-700 mb-1">שם משתמש / ID</label>
             <div className="relative">
               <User className="w-5 h-5 absolute top-2.5 right-3 text-slate-400" />
               <input type="text" className="w-full p-2 pr-10 border rounded focus:ring-2 focus:ring-blue-500 outline-none" placeholder="הזן שם משתמש..." value={username} onChange={(e) => setUsername(e.target.value)} />
             </div>
          </div>
          <div>
             <label className="block text-sm font-bold text-slate-700 mb-1">סיסמה</label>
             <div className="relative">
               <Lock className="w-5 h-5 absolute top-2.5 right-3 text-slate-400" />
               <input type="password" className="w-full p-2 pr-10 border rounded focus:ring-2 focus:ring-blue-500 outline-none" placeholder="הזן סיסמה..." value={password} onChange={(e) => setPassword(e.target.value)} />
             </div>
          </div>
          {error && <div className="text-red-500 text-sm text-center font-bold">{error}</div>}
          <button
            type="submit"
            className="w-full bg-slate-900 text-white py-3 rounded font-bold hover:bg-slate-800 transition shadow-lg disabled:opacity-50"
            disabled={loading}
          >
            {loading ? 'מתחבר...' : 'כניסה למערכת'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginScreen;
