import React, { useMemo, useState } from 'react';
import { LiteratureResource } from '../types';
import { downloadLiterature, searchLiterature, summarizeLiterature } from '../services/literatureApi';

interface Props {
  knowledgeId: string;
  resources: LiteratureResource[];
  onRefresh: () => Promise<void>;
}

const oaLabel = (status?: string): string => {
  if (status === 'open') return 'Open Access';
  if (status === 'no_oa') return 'No OA';
  if (status === 'closed') return 'Closed';
  return status || 'unknown';
};

const summaryQualityLabel: Record<string, string> = {
  good: 'סיכום מלא',
  partial: 'סיכום חלקי',
  failed: 'סיכום נכשל',
  unknown: 'לא זמין',
};

const LiteraturePanel: React.FC<Props> = ({ knowledgeId, resources, onRefresh }) => {
  const [loadingAction, setLoadingAction] = useState<'search' | 'download' | 'summarize' | null>(null);
  const [error, setError] = useState<string | null>(null);

  const sortedResources = useMemo(
    () =>
      [...(resources ?? [])].sort((a, b) => (b.fetchedAt ?? '').localeCompare(a.fetchedAt ?? '')),
    [resources],
  );

  const handleAction = async (action: 'search' | 'download' | 'summarize', targetId?: string) => {
    try {
      setLoadingAction(action);
      setError(null);
      if (action === 'search') {
        await searchLiterature(knowledgeId);
      } else if (action === 'download') {
        await downloadLiterature(knowledgeId, targetId ? [targetId] : undefined);
      } else if (action === 'summarize') {
        await summarizeLiterature(knowledgeId, targetId ? [targetId] : undefined);
      }
      await onRefresh();
    } catch (err: any) {
      setError(err?.message ?? 'literature_action_failed');
    } finally {
      setLoadingAction(null);
    }
  };

  return (
    <div className="rounded border border-slate-200 bg-white">
      <div className="flex items-center justify-between border-b border-slate-100 px-4 py-3">
        <div>
          <h3 className="text-sm font-bold text-slate-700">מאמרים תומכים</h3>
          <p className="text-xs text-slate-500">מקורות חיצוניים לחיזוק טענות רפואיות</p>
        </div>
        <button
          type="button"
          className="rounded border border-blue-600 px-3 py-1 text-xs font-semibold text-blue-600 hover:bg-blue-50"
          onClick={() => handleAction('search')}
          disabled={loadingAction === 'search'}
        >
          {loadingAction === 'search' ? 'מחפש...' : 'חפש מאמרים'}
        </button>
      </div>
      {error && <div className="border-b border-rose-100 bg-rose-50 px-4 py-2 text-xs text-rose-700">{error}</div>}
      <div className="p-4 space-y-3 text-sm text-slate-700">
        {!sortedResources.length && (
          <div className="rounded border border-dashed border-slate-200 bg-slate-50 px-3 py-2 text-sm text-slate-500">
            טרם נמצאו מאמרים. נסה להריץ חיפוש.
          </div>
        )}
        {sortedResources.map((resource) => (
          <div key={resource.id} className="rounded border border-slate-100 bg-slate-50 p-3 space-y-2">
            <div className="flex items-center justify-between">
              <div>
                <div className="font-semibold text-slate-800">{resource.title}</div>
                <div className="text-xs text-slate-500">
                  {resource.journal || 'כתב עת לא ידוע'} · {resource.year ?? '—'}
                </div>
              </div>
              <div className="text-xs font-semibold text-slate-600">{oaLabel(resource.oaStatus)}</div>
            </div>
            <div className="flex flex-wrap gap-2">
              {resource.oaStatus === 'open' && (
                <button
                  type="button"
                  className="rounded border border-green-600 px-2 py-1 text-xs text-green-700 hover:bg-green-50"
                  onClick={() => handleAction('download', resource.id)}
                  disabled={loadingAction === 'download' || resource.downloadStatus === 'downloaded'}
                >
                  {resource.downloadStatus === 'downloaded' ? 'הורד' : 'הורד PDF'}
                </button>
              )}
              {resource.downloadStatus === 'downloaded' && (
                <button
                  type="button"
                  className="rounded border border-slate-600 px-2 py-1 text-xs text-slate-700 hover:bg-slate-100"
                  onClick={() => handleAction('summarize', resource.id)}
                  disabled={loadingAction === 'summarize'}
                >
                  {resource.summaryJson ? 'סוכם' : 'סכם'}
                </button>
              )}
              {resource.url && (
                <a
                  href={resource.url}
                  target="_blank"
                  rel="noreferrer"
                  className="rounded border border-slate-300 px-2 py-1 text-xs text-slate-600 hover:bg-white"
                >
                  פתח מקור
                </a>
              )}
            </div>
            {resource.linkedClaimIds && resource.linkedClaimIds.length > 0 && (
              <div className="text-[11px] text-slate-500">
                קשור לטענות: {resource.linkedClaimIds.join(', ')}
              </div>
            )}
            {resource.summaryQuality && resource.summaryQuality !== 'unknown' && (
              <div className="text-[11px] font-semibold text-slate-600">
                איכות סיכום: {summaryQualityLabel[resource.summaryQuality] ?? resource.summaryQuality}
                {resource.summaryQualityNote ? ` – ${resource.summaryQualityNote}` : ''}
              </div>
            )}
            {resource.summaryJson && (
              <div className="rounded border border-slate-200 bg-white p-3 text-xs text-slate-700 space-y-2">
                <div>
                  <div className="font-semibold text-slate-800">תקציר</div>
                  <p>{resource.summaryJson.summary}</p>
                </div>
                {!!resource.summaryJson.keyFindings?.length && (
                  <div>
                    <div className="font-semibold text-slate-800">ממצאים עיקריים</div>
                    <ul className="list-disc space-y-1 pl-4">
                      {resource.summaryJson.keyFindings.map((finding: string, index: number) => (
                        <li key={`${resource.id}-finding-${index}`}>{finding}</li>
                      ))}
                    </ul>
                  </div>
                )}
                {!!resource.summaryJson.bottomLine && (
                  <div>
                    <div className="font-semibold text-slate-800">Bottom Line</div>
                    <p>{resource.summaryJson.bottomLine}</p>
                  </div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default LiteraturePanel;
